+:root {
+  --bg: #0f172a;
+  --panel: #111827;
+  --card: #0b1220;
+  --accent: #7dd3fc;
+  --accent-2: #a855f7;
+  --text: #e2e8f0;
+  --muted: #94a3b8;
+  --border: #1f2937;
+}
+
+* { box-sizing: border-box; }
+body {
+  margin: 0;
+  font-family: "Inter", system-ui, -apple-system, sans-serif;
+  background: radial-gradient(circle at 20% 20%, #0b1325 0, #0a1020 35%, #080c1a 60%, #050910 100%);
+  color: var(--text);
+}
+
+.topbar {
+  display: flex;
+  justify-content: space-between;
+  align-items: center;
+  padding: 14px 18px;
+  border-bottom: 1px solid var(--border);
+  background: rgba(8, 12, 26, 0.7);
+  backdrop-filter: blur(8px);
+  position: sticky;
+  top: 0;
+  z-index: 10;
+}
+.topbar .left, .topbar .right { display: flex; gap: 10px; align-items: center; }
+
+.layout {
+  display: grid;
+  grid-template-columns: 1.2fr 1fr;
+  gap: 12px;
+  padding: 12px;
+}
+
+.map-panel, .timeline-panel {
+  background: var(--panel);
+  border: 1px solid var(--border);
+  border-radius: 12px;
+  padding: 12px;
+  box-shadow: 0 10px 40px rgba(0,0,0,0.35);
+}
+
+.panel-header {
+  display: flex;
+  justify-content: space-between;
+  align-items: center;
+  margin-bottom: 10px;
+}
+
+.map {
+  position: relative;
+  height: 360px;
+  background: linear-gradient(135deg, #0b1220, #0d1628);
+  border-radius: 10px;
+  border: 1px solid var(--border);
+  overflow: hidden;
+}
+.map::after {
+  content: "";
+  position: absolute;
+  inset: 0;
+  background: radial-gradient(circle at 30% 30%, rgba(125,211,252,0.06), transparent 50%),
+              radial-gradient(circle at 70% 60%, rgba(168,85,247,0.07), transparent 50%);
+}
+.pin {
+  position: absolute;
+  width: 34px;
+  height: 34px;
+  border-radius: 50%;
+  background: var(--accent);
+  color: #0b1220;
+  display: grid;
+  place-items: center;
+  font-weight: 700;
+  cursor: pointer;
+  border: 2px solid #0b1220;
+  transition: transform 120ms ease, box-shadow 120ms ease;
+}
+.pin:hover {
+  transform: translateY(-2px);
+  box-shadow: 0 8px 18px rgba(125,211,252,0.25);
+}
+
+.timeline {
+  display: flex;
+  flex-direction: column;
+  gap: 10px;
+  max-height: 420px;
+  overflow-y: auto;
+  padding-right: 4px;
+}
+.card {
+  padding: 12px;
+  border-radius: 10px;
+  background: var(--card);
+  border: 1px solid var(--border);
+  transition: border-color 120ms ease, transform 120ms ease;
+  cursor: pointer;
+}
+.card:hover {
+  border-color: var(--accent);
+  transform: translateY(-1px);
+}
+.card.active {
+  border-color: var(--accent);
+  box-shadow: 0 8px 24px rgba(125,211,252,0.2);
+}
+.meta {
+  display: flex;
+  gap: 8px;
+  align-items: center;
+  font-size: 12px;
+  color: var(--muted);
+}
+.thumbs { display: flex; gap: 6px; margin-top: 8px; }
+.thumb {
+  width: 52px;
+  height: 42px;
+  border-radius: 6px;
+  background: linear-gradient(145deg, #1a2335, #111827);
+  border: 1px solid var(--border);
+}
+
+.badge {
+  display: inline-flex;
+  align-items: center;
+  gap: 6px;
+  padding: 2px 8px;
+  border-radius: 999px;
+  font-size: 12px;
+  background: rgba(148,163,184,0.12);
+  color: var(--muted);
+}
+.badge.offline { background: rgba(125,211,252,0.12); color: var(--accent); }
+.badge.draft { background: rgba(168,85,247,0.14); color: var(--accent-2); }
+
+.primary, .ghost, .switcher {
+  border: 1px solid transparent;
+  border-radius: 10px;
+  padding: 10px 14px;
+  font-weight: 700;
+  cursor: pointer;
+  color: var(--text);
+  background: linear-gradient(135deg, #1a2335, #131b2b);
+}
+.primary {
+  background: linear-gradient(135deg, #7dd3fc, #38bdf8);
+  color: #08101f;
+  border: none;
+}
+.ghost {
+  background: transparent;
+  border-color: var(--border);
+  color: var(--muted);
+}
+.switcher {
+  background: linear-gradient(135deg, #a855f7, #7c3aed);
+  color: #0f0a1a;
+  border: none;
+}
+.block { width: 100%; }
+
+.muted { color: var(--muted); }
+.hint { color: var(--muted); font-size: 13px; margin: 8px 0 0; }
+.status { color: var(--muted); font-size: 12px; }
+
+.modal {
+  position: fixed;
+  inset: 0;
+  display: grid;
+  place-items: center;
+  background: rgba(0,0,0,0.45);
+  z-index: 20;
+}
+.modal.hidden { display: none; }
+.modal-content {
+  width: min(420px, 90vw);
+  background: var(--panel);
+  border: 1px solid var(--border);
+  border-radius: 12px;
+  padding: 16px;
+  box-shadow: 0 18px 50px rgba(0,0,0,0.5);
+}
+.modal-header {
+  display: flex;
+  justify-content: space-between;
+  align-items: center;
+  margin-bottom: 6px;
+}
+.capture-actions { display: grid; gap: 8px; margin: 12px 0; }
+.metadata { color: var(--muted); font-size: 14px; display: grid; gap: 4px; }
+
+.trip-list { list-style: none; padding: 0; margin: 12px 0; display: grid; gap: 8px; }
+.trip-item {
+  padding: 10px 12px;
+  background: var(--card);
+  border: 1px solid var(--border);
+  border-radius: 8px;
+  cursor: pointer;
+}
+.trip-item.selected { border-color: var(--accent); color: var(--accent); }
+
+@media (max-width: 900px) {
+  .layout { grid-template-columns: 1fr; }
+  .topbar { flex-direction: column; gap: 10px; }
+}
