"use client";

import { useState } from "react";
import { useRouter } from "next/navigation";

type FieldErrors = {
  title?: string;
  startDate?: string;
  endDate?: string;
  coverImageUrl?: string;
  form?: string;
};

const CreateTripForm = () => {
  const router = useRouter();
  const [title, setTitle] = useState("");
  const [startDate, setStartDate] = useState("");
  const [endDate, setEndDate] = useState("");
  const [coverImageUrl, setCoverImageUrl] = useState("");
  const [errors, setErrors] = useState<FieldErrors>({});
  const [submitting, setSubmitting] = useState(false);

  const validate = () => {
    const nextErrors: FieldErrors = {};

    if (!title.trim()) {
      nextErrors.title = "Title is required.";
    }

    if (!startDate) {
      nextErrors.startDate = "Start date is required.";
    }

    if (!endDate) {
      nextErrors.endDate = "End date is required.";
    }

    if (startDate && endDate) {
      const start = new Date(startDate);
      const end = new Date(endDate);
      if (start > end) {
        nextErrors.endDate = "End date must be after the start date.";
      }
    }

    if (coverImageUrl.trim() && !coverImageUrl.startsWith("http")) {
      nextErrors.coverImageUrl = "Cover image must be a valid URL.";
    }

    return nextErrors;
  };

  const handleSubmit = async (event: React.FormEvent<HTMLFormElement>) => {
    event.preventDefault();
    setErrors({});

    const nextErrors = validate();
    if (Object.keys(nextErrors).length > 0) {
      setErrors(nextErrors);
      return;
    }

    setSubmitting(true);

    const payload = {
      title: title.trim(),
      startDate,
      endDate,
      coverImageUrl: coverImageUrl.trim() || undefined,
    };

    try {
      const response = await fetch("/api/trips", {
        method: "POST",
        headers: {
          "Content-Type": "application/json",
        },
        body: JSON.stringify(payload),
      });

      const result = await response.json();

      if (!response.ok || result.error) {
        setErrors({
          form: result.error?.message ?? "Unable to create trip. Please try again.",
        });
        setSubmitting(false);
        return;
      }

      router.push(`/trips/${result.data.id}`);
    } catch {
      setErrors({
        form: "Unable to create trip. Please try again.",
      });
      setSubmitting(false);
    }
  };

  return (
    <form className="mt-6 space-y-4" onSubmit={handleSubmit}>
      <label className="block text-sm text-[#2D2A26]">
