import re
from pathlib import Path

project_root = Path('/Users/tommy/Development/TravelBlogs')
output_folder = project_root / '_bmad-output'
status_file = output_folder / 'implementation-artifacts' / 'sprint-status.yaml'
epics_path = output_folder / 'epics.md'

# Load epics
text = epics_path.read_text()

lines = text.splitlines()
current_epic = None
epics = []
for line in lines:
    m = re.match(r'^## Epic (\d+):\s*(.+)$', line)
    if m:
        if current_epic:
            epics.append(current_epic)
        current_epic = {'num': int(m.group(1)), 'title': m.group(2).strip(), 'stories': []}
        continue
    m = re.match(r'^### Story (\d+)\.(\d+):\s*(.+)$', line)
    if m and current_epic:
        epic_num = int(m.group(1))
        story_num = int(m.group(2))
        title = m.group(3).strip()
        current_epic['stories'].append({'epic': epic_num, 'story': story_num, 'title': title})

if current_epic:
    epics.append(current_epic)

def kebab(s):
    s = s.lower()
    s = re.sub(r'[^a-z0-9\s-]', '', s)
    s = re.sub(r'\s+', '-', s.strip())
    return s

expected_epics = []
expected_stories = []
expected_retros = []

for epic in epics:
    expected_epics.append(f"epic-{epic['num']}")
    expected_retros.append(f"epic-{epic['num']}-retrospective")
    for st in epic['stories']:
        expected_stories.append(f"{st['epic']}-{st['story']}-{kebab(st['title'])}")

# Load status file
status_lines = status_file.read_text().splitlines()
status_map = {}
for line in status_lines:
    m = re.match(r'^\s*([^:#\s][^:]*):\s*(\S+)\s*$', line)
    if m:
        key = m.group(1).strip()
        val = m.group(2).strip()
        if key in {'generated','project','project_key','tracking_system','story_location'}:
            continue
        if key == 'development_status':
            continue
        # Only consider indented entries
        if line.startswith('  '):
            status_map[key] = val

status_keys = set(status_map.keys())
expected_keys = set(expected_epics + expected_stories + expected_retros)

missing = sorted(expected_keys - status_keys)
extra = sorted(status_keys - expected_keys)

legal_story = {'backlog','ready-for-dev','in-progress','review','done'}
legal_epic = {'backlog','in-progress','done'}
legal_retro = {'optional','done'}

illegal = []
for key, val in status_map.items():
    if key.startswith('epic-') and key.endswith('-retrospective'):
        if val not in legal_retro:
            illegal.append((key,val))
    elif key.startswith('epic-'):
        if val not in legal_epic:
            illegal.append((key,val))
    else:
        if val not in legal_story:
            illegal.append((key,val))

# Counts
story_count = len(expected_stories)
epic_count = len(expected_epics)
epics_in_progress = sum(1 for k in expected_epics if status_map.get(k) == 'in-progress')
completed_stories = sum(1 for k in expected_stories if status_map.get(k) == 'done')

print('VALIDATION')
print('missing', len(missing))
print('extra', len(extra))
print('illegal', len(illegal))
print('epic_count', epic_count)
print('story_count', story_count)
print('epics_in_progress', epics_in_progress)
print('done_count', completed_stories)
if missing:
    print('MISSING_LIST', ','.join(missing))
if extra:
    print('EXTRA_LIST', ','.join(extra))
if illegal:
    print('ILLEGAL_LIST', ';'.join([f"{k}:{v}" for k,v in illegal]))
