  return { email, password };
};

export const validateCredentials = (
  email: string,
  password: string,
): CreatorUser | null => {
  const config = readCreatorConfig();

  if (!config.email || !config.password) {
    return null;
  }

  if (email !== config.email || password !== config.password) {
    return null;
  }

  return { ...creatorUser, email: config.email };
};

