      nextErrors.endDate = "End date must be after the start date.";
    }
  }

  if (values.coverImageUrl.trim() && !values.coverImageUrl.startsWith("http")) {
    nextErrors.coverImageUrl = "Cover image must be a valid URL.";
  }

  return nextErrors;
};

