"use client";

import { useEffect, useMemo, useState } from "react";
import Image from "next/image";

import {
  extractInlineImageUrls,
  getPhotoTimestamp,
  parseEntryContent,
} from "../../utils/entry-content";

type EntryMedia = {
  id: string;
  url: string;
  createdAt: string;
};

type EntryDetail = {
  id: string;
  tripId: string;
  text: string;
  createdAt: string;
  updatedAt: string;
  media: EntryMedia[];
};

type EntryDetailProps = {
  tripId: string;
  entryId: string;
};

const formatDate = (value: string) =>
  new Date(value).toLocaleDateString("en-US", {
    month: "short",
    day: "numeric",
    year: "numeric",
  });

const isOptimizedImage = (url: string) => url.startsWith("/");

const EntryDetail = ({ tripId, entryId }: EntryDetailProps) => {
  const [entry, setEntry] = useState<EntryDetail | null>(null);
  const [error, setError] = useState<string | null>(null);
  const [isLoading, setIsLoading] = useState(true);

  useEffect(() => {
    let isActive = true;
    setIsLoading(true);
    setError(null);

    const loadEntry = async () => {
      try {
        const response = await fetch(`/api/entries/${entryId}`, {
          method: "GET",
          credentials: "include",
          cache: "no-store",
        });
        const body = await response.json().catch(() => null);

        if (!response.ok || body?.error) {
          throw new Error(body?.error?.message ?? "Unable to load entry.");
        }

        if (isActive) {
          setEntry((body?.data as EntryDetail) ?? null);
          setIsLoading(false);
        }
      } catch (err) {
        if (isActive) {
          setEntry(null);
          setError(
            err instanceof Error ? err.message : "Unable to load entry.",
          );
          setIsLoading(false);
        }
      }
    };

    loadEntry();

    return () => {
      isActive = false;
    };
  }, [entryId]);

  const contentBlocks = useMemo(
    () => (entry ? parseEntryContent(entry.text) : []),
    [entry],
  );
  const inlineImageUrls = useMemo(
    () => (entry ? extractInlineImageUrls(entry.text) : []),
    [entry],
  );
  const galleryItems = useMemo(() => {
    if (!entry) {
      return [];
    }

    const items = new Map<
      string,
      { url: string; createdAt?: string; id?: string }
    >();

    entry.media.forEach((item) => {
      items.set(item.url, {
        url: item.url,
        createdAt: item.createdAt,
        id: item.id,
      });
    });

    inlineImageUrls.forEach((url) => {
      if (!items.has(url)) {
        items.set(url, { url });
      }
    });

    return Array.from(items.values()).sort((left, right) => {
      const leftTimestamp = getPhotoTimestamp(left.url, left.createdAt);
      const rightTimestamp = getPhotoTimestamp(right.url, right.createdAt);
      return leftTimestamp - rightTimestamp;
    });
  }, [entry, inlineImageUrls]);

  if (isLoading) {
    return (
      <div className="min-h-screen bg-[#FBF7F1] px-6 py-12">
        <main className="mx-auto w-full max-w-3xl space-y-6">
          <a
            href={`/trips/${tripId}`}
            className="text-sm text-[#1F6F78] hover:underline"
          >
            â† Back to trip
          </a>
          <section className="rounded-2xl border border-black/10 bg-white p-8 shadow-sm">
            <p className="text-sm text-[#6B635B]">Loading entryâ€¦</p>
          </section>
        </main>
      </div>
    );
  }

  if (error) {
