export const COVER_IMAGE_MAX_BYTES = 5 * 1024 * 1024;
export const COVER_IMAGE_FIELD_NAME = "file";
export const COVER_IMAGE_PUBLIC_PATH = "/uploads/trips";

export const COVER_IMAGE_ALLOWED_MIME_TYPES = [
