                className={`group relative h-28 overflow-hidden rounded-xl border transition ${
                  coverImageUrl === url
                    ? "border-[#1F6F78] ring-2 ring-[#1F6F78]/30"
                    : "border-black/10 hover:border-[#1F6F78]/40"
