type FieldErrors = {
  date?: string;
  title?: string;
  text?: string;
  media?: string;
  form?: string;
};

type UploadStatus = "idle" | "uploading" | "success" | "failed";

type UploadItem = {
  id: string;
  file: File;
  previewUrl?: string;
  status: UploadStatus;
  progress: number;
  message?: string;
  url?: string;
  canRetry: boolean;
};

type EditEntryFormProps = {
  tripId: string;
  entryId: string;
  initialEntryDate: string;
  initialTitle: string;
  initialCoverImageUrl?: string | null;
  initialText: string;
  initialMediaUrls: string[];
};

const isValidEntryDate = (value: string) =>
  Boolean(value) && !Number.isNaN(Date.parse(value));

const maxTitleLength = 80;

const toDateInputValue = (value: string) => {
  const parsed = new Date(value);
  return Number.isNaN(parsed.getTime())
    ? ""
    : parsed.toISOString().slice(0, 10);
};

const getErrors = (
  entryDate: string,
  title: string,
  text: string,
  mediaUrls: string[],
  inlineImageUrls: string[],
) => {
  const nextErrors: FieldErrors = {};

  if (!isValidEntryDate(entryDate)) {
    nextErrors.date = "Entry date is required.";
  }

  if (!title.trim()) {
    nextErrors.title = "Entry title is required.";
  } else if (title.trim().length > maxTitleLength) {
    nextErrors.title = "Entry title must be 80 characters or fewer.";
  }

  if (!text.trim()) {
    nextErrors.text = "Entry text is required.";
