    71	export const insertInlineImageAtCursor = (
    72	  text: string,
    73	  url: string,
    74	  selectionStart: number,
    75	  selectionEnd: number,
    76	) => {
    77	  const safeStart = Math.max(0, Math.min(selectionStart, text.length));
    78	  const safeEnd = Math.max(safeStart, Math.min(selectionEnd, text.length));
    79	  const before = text.slice(0, safeStart);
    80	  const after = text.slice(safeEnd);
    81	  const alt = findInlineImageAlt(text, url) ?? DEFAULT_INLINE_ALT;
    82	  const snippet = `![${alt}](${url})`;
    83	  const needsLeadingBreak = before.length > 0 && !before.endsWith("\n");
    84	  const needsTrailingBreak = after.length > 0 && !after.startsWith("\n");
    85	  const formattedSnippet = `${needsLeadingBreak ? "\n" : ""}${snippet}${needsTrailingBreak ? "\n" : ""}`;
    86	
    87	  return {
    88	    nextText: before + formattedSnippet + after,
    89	    nextCursor: safeStart + formattedSnippet.length,
    90	  };
    91	};
