  }, [mediaPreviews.length]);

  const isOptimizedImage = (url: string) => url.startsWith("/");

  const renderUploadItems = (
    items: UploadItem[],
    onRetry: (item: UploadItem) => void,
    onRemove: (item: UploadItem) => void,
  ) => {
    if (items.length === 0) {
