            </div>
          </header>

          <div className="mt-6 space-y-4">
            {contentBlocks.map((block, index) => {
              if (block.type === "text") {
                if (!block.value.trim()) {
                  return null;
                }
                return (
                  <p
                    key={`text-${index}`}
                    className="whitespace-pre-wrap text-sm text-[#2D2A26]"
                  >
                    {block.value}
                  </p>
                );
              }

              return (
                <div
                  key={`image-${block.url}-${index}`}
