import { findInlineImageAlt } from "./entry-content";

export type EntryApiMedia = {
  id: string;
  url: string;
  createdAt?: string;
};

export type EntryApiData = {
  id: string;
  tripId: string;
  title: string;
  text: string;
  createdAt: string;
  media: EntryApiMedia[];
};

export type EntryReaderMedia = {
  id: string;
  url: string;
  type: "image" | "video" | null;
  width: number | null;
  height: number | null;
  alt: string | null;
};

export type EntryReaderData = {
  id: string;
  title: string;
  body: string;
  createdAt: string;
  media: EntryReaderMedia[];
};

const imageExtensions = new Set(["jpg", "jpeg", "png", "webp", "gif", "avif"]);
const videoExtensions = new Set(["mp4", "webm", "mov", "m4v"]);

const inferMediaType = (url: string): "image" | "video" | null => {
  const extension = url.split(".").pop()?.toLowerCase();
  if (!extension) {
    return null;
  }
  if (imageExtensions.has(extension)) {
    return "image";
  }
  if (videoExtensions.has(extension)) {
    return "video";
  }
  return null;
};

export const mapEntryToReader = (entry: EntryApiData): EntryReaderData => {
  return {
    id: entry.id,
    title: entry.title,
    body: entry.text,
    createdAt: entry.createdAt,
    media: entry.media.map((item) => ({
      id: item.id,
      url: item.url,
      type: inferMediaType(item.url),
      width: null,
      height: null,
      alt: findInlineImageAlt(entry.text, item.url),
    })),
  };
};

