  _request: Request,
  { params }: { params: Promise<{ id: string }> | { id: string } },
) => {
  try {
    const { id } = await params;

    const trip = await prisma.trip.findUnique({
      where: {
        id,
      },
      select: {
        id: true,
        title: true,
        startDate: true,
        endDate: true,
        coverImageUrl: true,
        entries: {
          orderBy: {
