                }

                return block.value
                  .split(/\n{2,}/)
                  .map((paragraph) => paragraph.trim())
                  .filter(Boolean)
                  .map((paragraph, paragraphIndex) => (
                    <p
                      key={`entry-paragraph-${index}-${paragraphIndex}`}
                      className="text-[17px] leading-7 text-[#2D2A26]"
