import { useState } from "react";

import UserForm from "./user-form";
import UserList from "./user-list";

type UserListItem = {
  id: string;
  email: string;
  name: string;
  role: "creator" | "viewer";
  isActive: boolean;
  createdAt: string;
};

type UsersDashboardProps = {
  users: UserListItem[];
};

const UsersDashboard = ({ users }: UsersDashboardProps) => {
  const [showForm, setShowForm] = useState(false);

  return (
