  endDate: string;
  coverImageUrl: string | null;
};

type TripDetailProps = {
  tripId: string;
};

type EntryMedia = {
  id: string;
  url: string;
  createdAt: string;
};

type ViewerUser = {
  id: string;
  name: string;
  email: string;
};

type ViewerOption = {
  id: string;
  name: string;
  email: string;
};

type ViewerAccess = {
  id: string;
  tripId: string;
  userId: string;
  canContribute: boolean;
  createdAt: string;
  user: ViewerUser;
};

type EntrySummary = {
  id: string;
  tripId: string;
  title: string;
  coverImageUrl?: string | null;
  text: string;
  createdAt: string;
  updatedAt: string;
  media: EntryMedia[];
};

const formatDate = (value: string) =>
  new Date(value).toLocaleDateString("en-US", {
    month: "short",
    day: "numeric",
    year: "numeric",
  });

const formatEntryDate = (value: string) =>
  new Date(value).toLocaleDateString("en-US", {
    month: "short",
    day: "numeric",
    year: "numeric",
  });

const isOptimizedImage = (url: string) => url.startsWith("/");

const TripDetail = ({ tripId }: TripDetailProps) => {
  const [trip, setTrip] = useState<TripDetail | null>(null);
  const [error, setError] = useState<string | null>(null);
  const [isLoading, setIsLoading] = useState(true);
  const [entries, setEntries] = useState<EntrySummary[]>([]);
  const [entriesError, setEntriesError] = useState<string | null>(null);
  const [entriesLoading, setEntriesLoading] = useState(true);
  const [shareLink, setShareLink] = useState<string | null>(null);
  const [shareError, setShareError] = useState<string | null>(null);
  const [shareLoading, setShareLoading] = useState(false);
  const [shareCopied, setShareCopied] = useState(false);
  const [shareRevoked, setShareRevoked] = useState(false);
  const [isSharePanelOpen, setIsSharePanelOpen] = useState(false);
  const [viewers, setViewers] = useState<ViewerAccess[]>([]);
  const [viewersLoading, setViewersLoading] = useState(false);
  const [viewersError, setViewersError] = useState<string | null>(null);
  const [availableViewers, setAvailableViewers] = useState<ViewerOption[]>([]);
  const [availableViewersError, setAvailableViewersError] = useState<string | null>(null);
  const [selectedViewerId, setSelectedViewerId] = useState("");
  const [isViewerSelectOpen, setIsViewerSelectOpen] = useState(false);
  const [inviteError, setInviteError] = useState<string | null>(null);
  const [inviteSending, setInviteSending] = useState(false);
  const [removingViewerIds, setRemovingViewerIds] = useState<string[]>([]);
  const [isRevokeOpen, setIsRevokeOpen] = useState(false);
  const [isRevoking, setIsRevoking] = useState(false);
  const [revokeError, setRevokeError] = useState<string | null>(null);

  useEffect(() => {
    let isActive = true;
    setIsLoading(true);
    setError(null);

    const loadTrip = async () => {
      try {
        const response = await fetch(`/api/trips/${tripId}`, {
          method: "GET",
          credentials: "include",
          cache: "no-store",
        });
        const body = await response.json().catch(() => null);

        if (!response.ok || body?.error) {
          throw new Error(body?.error?.message ?? "Unable to load trip.");
        }

        if (isActive) {
          setTrip((body?.data as TripDetail) ?? null);
          setIsLoading(false);
        }
      } catch (err) {
        if (isActive) {
          setTrip(null);
          setError(
            err instanceof Error ? err.message : "Unable to load trip.",
          );
          setIsLoading(false);
        }
      }
    };

    loadTrip();

    return () => {
      isActive = false;
    };
  }, [tripId]);

  useEffect(() => {
    let isActive = true;
    setEntriesLoading(true);
    setEntriesError(null);

    const loadEntries = async () => {
      try {
        const response = await fetch(`/api/entries?tripId=${tripId}`, {
          method: "GET",
          credentials: "include",
          cache: "no-store",
        });
        const body = await response.json().catch(() => null);

        if (!response.ok || body?.error) {
          throw new Error(body?.error?.message ?? "Unable to load entries.");
        }

        if (isActive) {
          setEntries((body?.data as EntrySummary[]) ?? []);
          setEntriesLoading(false);
        }
      } catch (err) {
        if (isActive) {
          setEntries([]);
          setEntriesError(
            err instanceof Error ? err.message : "Unable to load entries.",
          );
          setEntriesLoading(false);
        }
      }
    };

    loadEntries();

    return () => {
      isActive = false;
    };
  }, [tripId]);

  useEffect(() => {
    let isActive = true;
    setShareError(null);
    setShareCopied(false);
    setShareRevoked(false);

    const loadShareLink = async () => {
      try {
        const response = await fetch(`/api/trips/${tripId}/share-link`, {
          method: "GET",
          credentials: "include",
          cache: "no-store",
        });
        if (response.status === 404) {
          if (isActive) {
            setShareLink(null);
          }
          return;
        }
        const body = await response.json().catch(() => null);
        if (!response.ok || body?.error) {
          throw new Error(body?.error?.message ?? "Unable to load share link.");
        }
        const shareUrl = body?.data?.shareUrl;
        if (isActive) {
          setShareLink(shareUrl ? (shareUrl as string) : null);
        }
      } catch (err) {
        if (isActive) {
          setShareError(
            err instanceof Error ? err.message : "Unable to load share link.",
          );
        }
      }
    };

    loadShareLink();

    return () => {
      isActive = false;
    };
  }, [tripId]);

  useEffect(() => {
    if (!isSharePanelOpen) {
      return;
    }

    let isActive = true;
    setViewersLoading(true);
    setViewersError(null);
    setAvailableViewersError(null);

    const loadViewers = async () => {
      try {
        const response = await fetch(`/api/trips/${tripId}/viewers`, {
          method: "GET",
          credentials: "include",
          cache: "no-store",
        });
        const body = await response.json().catch(() => null);

        if (!response.ok || body?.error) {
          throw new Error(
            body?.error?.message ?? "Unable to load invited viewers.",
          );
        }

        if (isActive) {
          setViewers((body?.data as ViewerAccess[]) ?? []);
          setViewersLoading(false);
        }
      } catch (err) {
        if (isActive) {
          setViewers([]);
          setViewersError(
            err instanceof Error
              ? err.message
              : "Unable to load invited viewers.",
          );
          setViewersLoading(false);
        }
      }
    };

    const loadAvailableViewers = async () => {
      try {
        const response = await fetch("/api/users", {
          method: "GET",
          credentials: "include",
          cache: "no-store",
        });
        const body = await response.json().catch(() => null);

        if (!response.ok || body?.error) {
          throw new Error(
            body?.error?.message ?? "Unable to load available viewers.",
          );
        }

        const users = (body?.data as Array<{
          id: string;
          name: string;
          email: string;
          role: string;
          isActive: boolean;
        }> | null) ?? [];

        const viewerOptions = users
          .filter((user) => user.isActive)
          .map((user) => ({
            id: user.id,
            name: user.name,
            email: user.email,
          }));

        if (isActive) {
          setAvailableViewers(viewerOptions);
        }
      } catch (err) {
        if (isActive) {
          setAvailableViewers([]);
          setAvailableViewersError(
            err instanceof Error
              ? err.message
              : "Unable to load available viewers.",
          );
        }
      }
    };

    loadViewers();
    loadAvailableViewers();

    return () => {
      isActive = false;
    };
  }, [isSharePanelOpen, tripId]);

  const entriesByDate = useMemo(() => {
    return [...entries].sort(
      (left, right) =>
        new Date(left.createdAt).getTime() - new Date(right.createdAt).getTime(),
    );
  }, [entries]);

  const invitedUserIds = useMemo(
    () => new Set(viewers.map((viewer) => viewer.userId)),
    [viewers],
  );

  const filteredAvailableViewers = useMemo(
    () =>
      availableViewers.filter((viewer) => !invitedUserIds.has(viewer.id)),
    [availableViewers, invitedUserIds],
  );

  const selectedViewerLabel = useMemo(() => {
    if (!selectedViewerId) {
      return "Select a viewerâ€¦";
    }
    const selected = availableViewers.find(
      (viewer) => viewer.id === selectedViewerId,
    );
    if (!selected) {
      return "Select a viewerâ€¦";
    }
    return `${selected.name} (${selected.email})`;
  }, [availableViewers, selectedViewerId]);

  if (isLoading) {
    return (
      <div className="min-h-screen bg-[#FBF7F1] px-6 py-12">
        <main className="mx-auto w-full max-w-3xl space-y-6">
          <Link
            href="/trips"
            className="text-sm text-[#1F6F78] hover:underline"
          >
            â† Back to trips
          </Link>
          <section className="rounded-2xl border border-black/10 bg-white p-8 shadow-sm">
            <p className="text-sm text-[#6B635B]">Loading tripâ€¦</p>
          </section>
        </main>
      </div>
    );
  }

  if (error) {
    return (
      <div className="min-h-screen bg-[#FBF7F1] px-6 py-12">
        <main className="mx-auto w-full max-w-3xl space-y-6">
          <Link
            href="/trips"
            className="text-sm text-[#1F6F78] hover:underline"
          >
            â† Back to trips
          </Link>
          <section className="rounded-2xl border border-black/10 bg-white p-8 shadow-sm">
            <p className="text-sm text-[#B34A3C]">{error}</p>
          </section>
        </main>
      </div>
    );
  }

  if (!trip) {
    return null;
  }

  const handleCreateShareLink = async () => {
    setShareLoading(true);
    setShareError(null);
    setShareCopied(false);
    setShareRevoked(false);

    try {
      const response = await fetch(`/api/trips/${trip.id}/share-link`, {
        method: "POST",
        credentials: "include",
      });
      const body = await response.json().catch(() => null);

      if (!response.ok || body?.error) {
        throw new Error(body?.error?.message ?? "Unable to create share link.");
      }

      const shareUrl = body?.data?.shareUrl;
      if (!shareUrl) {
        throw new Error("Share link response was incomplete.");
      }

      setShareLink(shareUrl as string);
    } catch (err) {
      setShareError(
        err instanceof Error ? err.message : "Unable to create share link.",
      );
    } finally {
      setShareLoading(false);
    }
  };

  const handleInviteViewer = async () => {
    if (!selectedViewerId) {
      setInviteError("Select a viewer to invite.");
      return;
    }

    const selectedViewer = availableViewers.find(
      (viewer) => viewer.id === selectedViewerId,
    );
    if (!selectedViewer) {
      setInviteError("Select a valid viewer.");
      return;
    }

    setInviteSending(true);
    setInviteError(null);

    try {
      const response = await fetch(`/api/trips/${trip.id}/viewers`, {
        method: "POST",
        credentials: "include",
        headers: {
          "Content-Type": "application/json",
        },
        body: JSON.stringify({ email: selectedViewer.email }),
      });
      const body = await response.json().catch(() => null);

      if (!response.ok || body?.error) {
        throw new Error(body?.error?.message ?? "Unable to invite viewer.");
      }

      const created = body?.data as ViewerAccess | undefined;
      if (created) {
        setViewers((prev) => {
          const existingIndex = prev.findIndex((item) => item.id === created.id);
          if (existingIndex >= 0) {
            const next = [...prev];
            next[existingIndex] = created;
            return next;
          }
          return [...prev, created];
        });
      }

      setSelectedViewerId("");
    } catch (err) {
      setInviteError(
        err instanceof Error ? err.message : "Unable to invite viewer.",
      );
    } finally {
      setInviteSending(false);
    }
  };

  const handleRemoveViewer = async (viewer: ViewerAccess) => {
    if (removingViewerIds.includes(viewer.userId)) {
      return;
    }

    setRemovingViewerIds((prev) => [...prev, viewer.userId]);
    setViewersError(null);

    try {
      const response = await fetch(
        `/api/trips/${trip.id}/viewers/${viewer.userId}`,
        {
          method: "DELETE",
          credentials: "include",
        },
      );
      const body = await response.json().catch(() => null);

      if (!response.ok || body?.error) {
        throw new Error(body?.error?.message ?? "Unable to remove viewer.");
      }

      setViewers((prev) => prev.filter((item) => item.id !== viewer.id));
    } catch (err) {
      setViewersError(
        err instanceof Error ? err.message : "Unable to remove viewer.",
      );
    } finally {
      setRemovingViewerIds((prev) =>
        prev.filter((id) => id !== viewer.userId),
      );
    }
  };

  const handleOpenRevoke = () => {
    setIsRevokeOpen(true);
    setRevokeError(null);
  };

  const handleCloseRevoke = () => {
    setIsRevokeOpen(false);
    setIsRevoking(false);
    setRevokeError(null);
  };

  const handleRevokeShareLink = async () => {
    if (!shareLink) {
      return;
    }

    setIsRevoking(true);
    setRevokeError(null);

    try {
      const response = await fetch(`/api/trips/${trip.id}/share-link`, {
        method: "DELETE",
        credentials: "include",
      });
      const body = await response.json().catch(() => null);

      if (!response.ok || body?.error) {
        throw new Error(body?.error?.message ?? "Unable to revoke share link.");
      }

      setShareLink(null);
      setShareRevoked(true);
      setShareCopied(false);
      setIsRevokeOpen(false);
      setIsRevoking(false);
    } catch (err) {
      setRevokeError(
        err instanceof Error ? err.message : "Unable to revoke share link.",
      );
      setIsRevoking(false);
    }
  };

  const handleCopyShareLink = async () => {
    if (!shareLink) {
      return;
    }

    try {
      await navigator.clipboard?.writeText(shareLink);
      setShareCopied(true);
      setShareError(null);
    } catch {
      setShareError("Unable to copy the share link.");
      setShareCopied(false);
    }
  };

  return (
    <div className="min-h-screen bg-[#FBF7F1] px-6 py-12">
      <main className="mx-auto w-full max-w-3xl space-y-6">
        <Link href="/trips" className="text-sm text-[#1F6F78] hover:underline">
          â† Back to trips
        </Link>

        <section className="rounded-2xl border border-black/10 bg-white p-8 shadow-sm">
          <header className="flex flex-wrap items-start justify-between gap-4">
            <div className="space-y-2">
              <p className="text-xs uppercase tracking-[0.2em] text-[#6B635B]">
                Trip overview
              </p>
              <h1 className="text-3xl font-semibold text-[#2D2A26]">
                {trip.title}
              </h1>
              <p className="text-sm text-[#6B635B]">
                {formatDate(trip.startDate)} â€“ {formatDate(trip.endDate)}
              </p>
            </div>
          </header>

          {trip.coverImageUrl && isCoverImageUrl(trip.coverImageUrl) ? (
            <div className="relative mt-6 h-56 w-full overflow-hidden rounded-2xl bg-[#F2ECE3]">
              <Image
                src={trip.coverImageUrl}
                alt={`Cover for ${trip.title}`}
                fill
                sizes="(min-width: 768px) 768px, 100vw"
                className="object-cover"
                loading="lazy"
              />
            </div>
          ) : null}

          <div className="mt-6 flex flex-wrap items-center gap-3 text-sm text-[#6B635B]">
            <div className="flex items-center gap-2">
              <span className="font-semibold text-[#2D2A26]">Owner:</span>
              <span>Creator</span>
            </div>
            <button
              type="button"
              onClick={() => setIsSharePanelOpen((prev) => !prev)}
              aria-label="Share trip"
              className="inline-flex items-center gap-2 rounded-full border border-black/10 bg-white px-3 py-1 text-xs font-semibold uppercase tracking-[0.2em] text-[#6B635B] transition hover:border-[#1F6F78]/40 hover:text-[#1F6F78]"
            >
              Share
            </button>
          </div>

          {isSharePanelOpen ? (
            <div className="mt-4 rounded-2xl border border-black/10 bg-[#FBF7F1] p-4 text-sm text-[#6B635B]">
              <p className="text-xs uppercase tracking-[0.2em] text-[#6B635B]">
                Share link
              </p>
              <p className="mt-2">
                Create a read-only link for this trip.
              </p>

              {shareError ? (
                <p className="mt-3 text-sm text-[#B34A3C]">{shareError}</p>
              ) : null}

              {shareRevoked && !shareLink ? (
                <p className="mt-3">Link revoked.</p>
              ) : null}

              {shareLink ? (
                <div className="mt-3 flex flex-wrap items-center gap-3">
                  <input
                    type="text"
                    value={shareLink}
                    readOnly
                    className="w-full flex-1 rounded-xl border border-black/10 bg-white px-3 py-2 text-sm text-[#2D2A26]"
                    aria-label="Share URL"
                  />
                  <button
                    type="button"
                    onClick={handleCopyShareLink}
                    className="rounded-xl border border-[#1F6F78] px-4 py-2 text-xs font-semibold uppercase tracking-[0.2em] text-[#1F6F78] transition hover:bg-[#1F6F78] hover:text-white"
                  >
                    {shareCopied ? "Copied" : "Copy link"}
                  </button>
                </div>
              ) : (
                <button
                  type="button"
                  onClick={handleCreateShareLink}
                  className="mt-3 rounded-xl bg-[#1F6F78] px-4 py-2 text-xs font-semibold uppercase tracking-[0.2em] text-white transition hover:bg-[#195C63] disabled:cursor-not-allowed disabled:opacity-70"
                  disabled={shareLoading}
                >
                  {shareLoading ? "Creatingâ€¦" : "Generate link"}
                </button>
              )}

              <div className="mt-6 border-t border-black/10 pt-4">
                <p className="text-xs uppercase tracking-[0.2em] text-[#6B635B]">
                  Invite viewers
                </p>
                <p className="mt-2">
                  Invite existing users to view this trip.
                </p>

                {availableViewersError ? (
                  <p className="mt-3 text-sm text-[#B34A3C]">
                    {availableViewersError}
                  </p>
                ) : null}

                {viewersError ? (
                  <p className="mt-3 text-sm text-[#B34A3C]">{viewersError}</p>
                ) : null}

                {viewersLoading ? (
                  <p className="mt-3 text-sm">Loading viewersâ€¦</p>
                ) : viewers.length === 0 ? (
                  <p className="mt-3 text-sm">No invited viewers yet.</p>
                ) : (
                  <ul className="mt-3 space-y-2 text-sm text-[#2D2A26]">
                    {viewers.map((viewer) => (
                      <li
                        key={viewer.id}
                        className="flex flex-wrap items-center justify-between gap-3"
                      >
                        <div className="flex flex-col">
                          <span className="font-semibold">
                            {viewer.user.name}
                          </span>
                          <span className="text-xs text-[#6B635B]">
                            {viewer.user.email}
                          </span>
                        </div>
                        <button
                          type="button"
                          onClick={() => handleRemoveViewer(viewer)}
                          className="rounded-xl border border-[#B64A3A] px-3 py-1 text-[10px] font-semibold uppercase tracking-[0.2em] text-[#B64A3A] transition hover:bg-[#B64A3A]/10 disabled:cursor-not-allowed disabled:opacity-70"
                          disabled={removingViewerIds.includes(viewer.userId)}
                        >
                          {removingViewerIds.includes(viewer.userId)
                            ? "Removingâ€¦"
                            : "Remove"}
                        </button>
                      </li>
                    ))}
                  </ul>
                )}

                <div className="mt-4 flex flex-wrap items-center gap-3">
                  <div className="relative w-full flex-1">
                    <button
                      type="button"
                      onClick={() => setIsViewerSelectOpen((prev) => !prev)}
                      className="flex w-full items-center justify-between rounded-xl border border-black/10 bg-white px-3 py-2 text-sm text-[#2D2A26]"
                      aria-label="Invite viewer"
                      aria-expanded={isViewerSelectOpen}
                    >
                      <span>{selectedViewerLabel}</span>
                      <span className="text-xs uppercase tracking-[0.2em] text-[#6B635B]">
                        Choose
                      </span>
                    </button>
                    {isViewerSelectOpen ? (
                      <div className="absolute left-0 right-0 z-10 mt-2 max-h-56 overflow-y-auto rounded-xl border border-black/10 bg-white shadow-lg">
                        {filteredAvailableViewers.length === 0 ? (
                          <p className="px-3 py-2 text-sm text-[#6B635B]">
                            All active users are invited
                          </p>
                        ) : (
                          filteredAvailableViewers.map((viewer) => (
                            <button
                              key={viewer.id}
                              type="button"
                              onClick={() => {
                                setSelectedViewerId(viewer.id);
                                setIsViewerSelectOpen(false);
                              }}
                              className="flex w-full flex-col items-start px-3 py-2 text-left text-sm text-[#2D2A26] hover:bg-[#FBF7F1]"
                            >
                              <span className="font-semibold">
                                {viewer.name}
                              </span>
                              <span className="text-xs text-[#6B635B]">
                                {viewer.email}
                              </span>
                            </button>
                          ))
                        )}
                      </div>
                    ) : null}
                  </div>
                  <button
                    type="button"
                    onClick={handleInviteViewer}
                    className="rounded-xl border border-[#1F6F78] px-4 py-2 text-xs font-semibold uppercase tracking-[0.2em] text-[#1F6F78] transition hover:bg-[#1F6F78] hover:text-white disabled:cursor-not-allowed disabled:opacity-70"
                    disabled={inviteSending}
                  >
                    {inviteSending ? "Invitingâ€¦" : "Invite"}
                  </button>
                </div>

                {inviteError ? (
                  <p className="mt-3 text-sm text-[#B34A3C]">{inviteError}</p>
                ) : null}
              </div>
            </div>
          ) : null}
        </section>

        <section className="rounded-2xl border border-black/10 bg-white p-8 shadow-sm">
          <div className="flex flex-wrap items-center justify-between gap-4">
            <Link
              href={`/trips/${trip.id}/entries/new`}
              className="rounded-xl bg-[#1F6F78] px-4 py-2 text-sm font-semibold text-white transition hover:bg-[#195C63]"
            >
              Add story
            </Link>
          </div>

          {entriesLoading ? (
            <p className="mt-4 text-sm text-[#6B635B]">Loading entriesâ€¦</p>
          ) : entriesError ? (
            <p className="mt-4 text-sm text-[#B34A3C]">{entriesError}</p>
          ) : entriesByDate.length === 0 ? (
            <p className="mt-4 text-sm text-[#6B635B]">
              No entries yet. Use the add story button to get started.
            </p>
          ) : (
            <div className="mt-4 space-y-3">
              {entriesByDate.map((entry) => {
                const preview = stripInlineImages(entry.text);
                const inlineImages = extractInlineImageUrls(entry.text);
                const previewText =
                  preview.length === 0
                    ? "Photo update"
                    : preview.length > 120
                      ? `${preview.slice(0, 120)}â€¦`
                      : preview;
                const displayTitle = entry.title?.trim()
                  ? entry.title
                  : previewText;
                const cardImageUrl =
                  entry.coverImageUrl?.trim() ||
                  entry.media[0]?.url ||
                  inlineImages[0];

                return (
                  <Link
                    key={entry.id}
                    href={`/trips/${trip.id}/entries/${entry.id}`}
                    className="flex items-center gap-4 rounded-xl border border-black/10 bg-white px-4 py-3 transition hover:border-[#1F6F78]/40 hover:shadow-sm"
                  >
                    {cardImageUrl ? (
                      <div
                        className="relative shrink-0 overflow-hidden rounded-xl border border-black/10 bg-[#F2ECE3]"
                        style={{ width: 139, height: 97 }}
