import { NextResponse } from "next/server";
import { getToken } from "next-auth/jwt";
import { z } from "zod";

import { prisma } from "../../../../../../utils/db";

export const runtime = "nodejs";

const jsonError = (status: number, code: string, message: string) => {
  return NextResponse.json(
    {
      data: null,
      error: { code, message },
    },
    { status },
  );
};

const getUserId = async (request: Request) => {
  try {
    const token = await getToken({ req: request });
    return token?.sub ?? null;
  } catch {
    return null;
  }
};

const paramsSchema = z.object({
  id: z.string().trim().min(1, "Trip id is required."),
  userId: z.string().trim().min(1, "User id is required."),
});

const requireCreatorTrip = async (
  request: Request,
  params: Promise<{ id: string; userId: string }> | { id: string; userId: string },
) => {
  const userId = await getUserId(request);
  if (!userId) {
    return { error: jsonError(401, "UNAUTHORIZED", "Authentication required.") };
  }
  if (userId !== "creator") {
    return { error: jsonError(403, "FORBIDDEN", "Creator access required.") };
  }

  const resolved = await params;
  const parsed = paramsSchema.safeParse(resolved);
  if (!parsed.success) {
    return {
      error: jsonError(
        400,
        "VALIDATION_ERROR",
        parsed.error.issues[0]?.message ?? "Trip id is required.",
      ),
    };
  }

  const trip = await prisma.trip.findUnique({
    where: { id: parsed.data.id },
    select: {
      id: true,
      ownerId: true,
    },
  });

  if (!trip) {
    return { error: jsonError(404, "NOT_FOUND", "Trip not found.") };
  }

  if (trip.ownerId !== userId) {
    return {
      error: jsonError(403, "FORBIDDEN", "Not authorized to manage this trip."),
    };
  }

  return { trip, userId: parsed.data.userId };
};

export const DELETE = async (
  request: Request,
  { params }: { params: Promise<{ id: string; userId: string }> | { id: string; userId: string } },
) => {
  try {
    const access = await requireCreatorTrip(request, params);
    if (access.error) {
      return access.error;
    }

    const existing = await prisma.tripAccess.findUnique({
      where: {
        tripId_userId: {
          tripId: access.trip.id,
          userId: access.userId,
        },
      },
      select: { id: true },
    });

    if (!existing) {
      return jsonError(404, "NOT_FOUND", "Viewer invite not found.");
    }

    await prisma.tripAccess.delete({
      where: {
        tripId_userId: {
          tripId: access.trip.id,
          userId: access.userId,
        },
      },
    });

    return NextResponse.json(
      {
        data: {
          removed: true,
          tripId: access.trip.id,
          userId: access.userId,
        },
        error: null,
      },
      { status: 200 },
    );
  } catch (error) {
    console.error("Failed to remove viewer", error);
    return jsonError(
      500,
      "INTERNAL_SERVER_ERROR",
      "Unable to remove viewer.",
    );
  }
};

