import { NextResponse } from "next/server";
import { getToken } from "next-auth/jwt";
import { z } from "zod";
import { hash } from "bcryptjs";
import { Prisma } from "@prisma/client";

import { prisma } from "../../../utils/db";

export const runtime = "nodejs";

const createUserSchema = z.object({
  email: z.string().trim().min(1, "Email is required.").email("Email must be valid."),
  name: z.string().trim().min(1, "Name is required."),
  role: z.enum(["creator", "viewer"]),
  password: z.string().trim().min(8, "Password must be at least 8 characters."),
});

const jsonError = (status: number, code: string, message: string) => {
  return NextResponse.json(
    {
      data: null,
      error: { code, message },
    },
    { status },
  );
};

const formatValidationError = (error: z.ZodError) => {
  const messages = Array.from(
    new Set(error.issues.map((issue) => issue.message).filter(Boolean)),
  );

  if (messages.length === 0) {
    return "User details are invalid.";
  }

  return messages.join(" ");
};

const getUserId = async (request: Request) => {
  try {
    const token = await getToken({ req: request });
    return token?.sub ?? null;
  } catch {
    return null;
  }
};

const requireAdmin = async (request: Request) => {
  const userId = await getUserId(request);
  if (!userId) {
    return { error: jsonError(401, "UNAUTHORIZED", "Authentication required.") };
  }
  if (userId !== "creator") {
    return { error: jsonError(403, "FORBIDDEN", "Admin access required.") };
  }
  return { userId };
};

export const GET = async (request: Request) => {
  try {
    const auth = await requireAdmin(request);
    if (auth.error) {
      return auth.error;
    }

    const users = await prisma.user.findMany({
      orderBy: {
        createdAt: "desc",
      },
      select: {
        id: true,
        email: true,
        name: true,
        role: true,
        isActive: true,
        createdAt: true,
        updatedAt: true,
      },
    });

    return NextResponse.json(
      {
        data: users.map((user) => ({
          id: user.id,
          email: user.email,
          name: user.name,
          role: user.role,
          isActive: user.isActive,
          createdAt: user.createdAt.toISOString(),
          updatedAt: user.updatedAt.toISOString(),
        })),
        error: null,
      },
      { status: 200 },
    );
  } catch (error) {
    console.error("Failed to list users", error);
    return jsonError(500, "INTERNAL_SERVER_ERROR", "Unable to load users.");
  }
};

export const POST = async (request: Request) => {
  try {
    const auth = await requireAdmin(request);
    if (auth.error) {
      return auth.error;
    }

    let body: unknown;
    try {
      body = await request.json();
    } catch {
      return jsonError(400, "INVALID_JSON", "Request body must be valid JSON.");
    }

    const parsed = createUserSchema.safeParse(body);
    if (!parsed.success) {
      return jsonError(
        400,
        "VALIDATION_ERROR",
        formatValidationError(parsed.error),
      );
    }

    const existing = await prisma.user.findUnique({
      where: { email: parsed.data.email },
    });
    if (existing) {
      return jsonError(409, "DUPLICATE_USER", "Email already exists.");
    }

    const passwordHash = await hash(parsed.data.password, 12);

    const user = await prisma.user.create({
      data: {
        email: parsed.data.email,
        name: parsed.data.name,
        role: parsed.data.role,
        passwordHash,
      },
    });

    return NextResponse.json(
      {
        data: {
          id: user.id,
          email: user.email,
          name: user.name,
          role: user.role,
          isActive: user.isActive,
          createdAt: user.createdAt.toISOString(),
          updatedAt: user.updatedAt.toISOString(),
        },
        error: null,
      },
      { status: 201 },
    );
  } catch (error) {
    if (error instanceof Prisma.PrismaClientKnownRequestError) {
      if (error.code === "P2002") {
        return jsonError(409, "DUPLICATE_USER", "Email already exists.");
      }
    }
    console.error("Failed to create user", error);
    return jsonError(500, "INTERNAL_SERVER_ERROR", "Unable to create user.");
  }
};

