    }

    const deletedEntry = await prisma.entry.delete({
      where: { id },
    });

    return NextResponse.json(
      {
        data: {
          id: deletedEntry.id,
        },
        error: null,
      },
      { status: 200 },
    );
  } catch (error) {
    console.error("Failed to delete entry", error);
    return jsonError(500, "INTERNAL_SERVER_ERROR", "Unable to delete entry.");
  }
