    return jsonError(
      500,
      "INTERNAL_SERVER_ERROR",
      "Unable to load viewers.",
    );
  }
};

export const POST = async (
  request: Request,
  { params }: { params: Promise<{ id: string }> | { id: string } },
) => {
  try {
    const access = await requireCreatorTrip(request, params);
    if (access.error) {
      return access.error;
    }

    let body: unknown;
    try {
      body = await request.json();
    } catch {
      return jsonError(400, "INVALID_JSON", "Request body must be valid JSON.");
    }

    const parsed = inviteSchema.safeParse(body);
    if (!parsed.success) {
      return jsonError(400, "VALIDATION_ERROR", "Invite details are invalid.");
    }

    const userLookup = parsed.data.userId
      ? { id: parsed.data.userId }
      : { email: parsed.data.email ?? "" };

    const user = await prisma.user.findUnique({
      where: userLookup,
    });

    if (!user || !user.isActive) {
      return jsonError(404, "NOT_FOUND", "User not found.");
    }

    if (user.role !== "viewer" && user.role !== "creator") {
      return jsonError(
        400,
        "VALIDATION_ERROR",
        "User must be a creator or viewer.",
      );
    }

    const existing = await prisma.tripAccess.findUnique({
      where: {
        tripId_userId: {
          tripId: access.trip.id,
          userId: user.id,
        },
      },
      include: {
        user: {
          select: {
            id: true,
            name: true,
            email: true,
          },
        },
      },
    });

    if (existing) {
      return NextResponse.json(
        {
          data: formatAccess(existing),
          error: null,
        },
        { status: 200 },
      );
    }

    const created = await prisma.tripAccess.create({
      data: {
        tripId: access.trip.id,
        userId: user.id,
      },
      include: {
        user: {
          select: {
            id: true,
            name: true,
            email: true,
          },
        },
      },
    });

    return NextResponse.json(
      {
        data: formatAccess(created),
        error: null,
      },
      { status: 201 },
    );
  } catch (error) {
    console.error("Failed to invite viewer", error);
    return jsonError(
      500,
      "INTERNAL_SERVER_ERROR",
      "Unable to invite viewer.",
    );
  }
};

