      setShareLink(null);
      setShareRevoked(true);
      setShareCopied(false);
      setIsRevokeOpen(false);
      setIsRevoking(false);
    } catch (err) {
      setRevokeError(
        err instanceof Error ? err.message : "Unable to revoke share link.",
      );
      setIsRevoking(false);
    }
  };

  const handleCopyShareLink = async () => {
    if (!shareLink) {
      return;
    }

    try {
      await navigator.clipboard?.writeText(shareLink);
      setShareCopied(true);
      setShareError(null);
