                          setIsInviteeMenuOpen((prev) => !prev)
                        }
                        className="flex w-full items-center justify-between rounded-xl border border-black/10 bg-white px-3 py-2 text-left text-sm text-[#2D2A26] transition hover:border-[#1F6F78]/40"
