                        aria-haspopup="listbox"
                        aria-expanded={isInviteeMenuOpen}
                        disabled={
                          eligibleLoading ||
                          filteredInvitees.length === 0 ||
                          Boolean(pendingRemoval)
                        }
                      >
                        <span className={selectedInvitee ? "" : "text-[#6B635B]"}>
                          {selectedInvitee
                            ? `${selectedInvitee.name} â€” ${selectedInvitee.email}`
                            : eligibleLoading
                              ? "Loading inviteesâ€¦"
                              : filteredInvitees.length === 0
                                ? "No eligible users"
                                : "Select invitee"}
                        </span>
                        <span className="text-xs text-[#6B635B]">â–¾</span>
                      </button>
                      {isInviteeMenuOpen &&
                      !pendingRemoval &&
                      filteredInvitees.length > 0 ? (
                        <div className="absolute z-10 mt-2 w-full rounded-2xl border border-black/10 bg-white p-2 shadow-lg">
                          <ul
                            role="listbox"
                            aria-label="Eligible invitees"
                            className="max-h-48 space-y-1 overflow-auto"
                          >
                            {filteredInvitees.map((invitee) => (
