export const GET = async (
  request: Request,
  { params }: { params: Promise<{ id: string }> | { id: string } },
) => {
  try {
    const { id } = await params;

    const userId = await getUserId(request);
    if (!userId) {
      return jsonError(401, "UNAUTHORIZED", "Authentication required.");
    }
    const entry = await prisma.entry.findUnique({
      where: {
        id,
      },
      include: {
        media: {
          orderBy: {
            createdAt: "asc",
          },
        },
        trip: true,
      },
    });

    if (!entry) {
      return jsonError(404, "NOT_FOUND", "Entry not found.");
    }

    if (entry.trip.ownerId !== userId) {
      const canView = await hasTripAccess(entry.tripId, userId);
      if (!canView) {
        return jsonError(403, "FORBIDDEN", "Not authorized to view this entry.");
      }
    }

    const [previousEntry, nextEntry] = await Promise.all([
      prisma.entry.findFirst({
        where: {
          tripId: entry.tripId,
          createdAt: {
            lt: entry.createdAt,
          },
        },
        orderBy: {
          createdAt: "desc",
        },
        select: {
          id: true,
          title: true,
          createdAt: true,
        },
      }),
      prisma.entry.findFirst({
        where: {
          tripId: entry.tripId,
          createdAt: {
            gt: entry.createdAt,
          },
        },
        orderBy: {
          createdAt: "asc",
        },
        select: {
          id: true,
          title: true,
          createdAt: true,
        },
      }),
    ]);

    return NextResponse.json(
