}) => ({
  id: access.id,
  tripId: access.tripId,
  userId: access.userId,
  canContribute: access.canContribute,
  createdAt: access.createdAt.toISOString(),
  user: {
    id: access.user.id,
    name: access.user.name,
    email: access.user.email,
  },
});

