  extractInlineImageUrls,
  findInlineImageAlt,
  getPhotoTimestamp,
  parseEntryContent,
} from "../../utils/entry-content";

type EntryMedia = {
  id: string;
  url: string;
  createdAt: string;
};

type EntryData = {
  id: string;
  tripId: string;
