"use client";

import { useState } from "react";
import { signIn } from "next-auth/react";
import { useRouter, useSearchParams } from "next/navigation";

const SignInPage = () => {
  const [error, setError] = useState<string | null>(null);
  const [submitting, setSubmitting] = useState(false);
  const router = useRouter();
  const searchParams = useSearchParams();
  const callbackUrl = searchParams.get("callbackUrl") ?? "/trips";
  const authError = searchParams.get("error");
  const authErrorMessage = authError
    ? authError === "CredentialsSignin"
      ? "Invalid email or password."
      : "Unable to sign in. Please try again."
    : null;

  const handleSubmit = async (event: React.FormEvent<HTMLFormElement>) => {
    event.preventDefault();
    setError(null);
    setSubmitting(true);

    const formData = new FormData(event.currentTarget);
    const email = String(formData.get("email") ?? "").trim();
    const password = String(formData.get("password") ?? "");

    if (!email || !password) {
      setError("Email and password are required.");
      setSubmitting(false);
      return;
    }

    const result = await signIn("credentials", {
      redirect: false,
      email,
      password,
      callbackUrl,
    });

    if (!result) {
      setError("Unable to sign in. Please try again.");
      setSubmitting(false);
      return;
    }

    if (result.error) {
      setError(
        result.error === "CredentialsSignin"
          ? "Invalid email or password."
          : "Unable to sign in. Please try again.",
      );
      setSubmitting(false);
      return;
    }

    router.push(result.url ?? callbackUrl);
  };

  return (
    <div className="flex min-h-screen items-center justify-center bg-[#FBF7F1] px-6">
      <main className="w-full max-w-md rounded-2xl border border-black/10 bg-white p-8 shadow-sm">
