import { useState } from "react";
import { useRouter } from "next/navigation";

type FieldErrors = {
  email?: string;
  name?: string;
  role?: string;
  password?: string;
  form?: string;
};

type FormValues = {
  email: string;
  name: string;
  role: "creator" | "viewer";
  password: string;
};

const emailPattern = /^[^\s@]+@[^\s@]+\.[^\s@]+$/;

const getErrors = (values: FormValues) => {
  const nextErrors: FieldErrors = {};

  if (!values.email.trim()) {
    nextErrors.email = "Email is required.";
  } else if (!emailPattern.test(values.email.trim())) {
    nextErrors.email = "Email must be valid.";
  }

  if (!values.name.trim()) {
    nextErrors.name = "Name is required.";
  }

  if (!values.role) {
    nextErrors.role = "Role is required.";
  }

  if (!values.password.trim()) {
    nextErrors.password = "Password is required.";
  } else if (values.password.trim().length < 8) {
    nextErrors.password = "Password must be at least 8 characters.";
  }

  return nextErrors;
};

const UserForm = () => {
  const router = useRouter();
  const [email, setEmail] = useState("");
  const [name, setName] = useState("");
  const [role, setRole] = useState<FormValues["role"]>("viewer");
  const [password, setPassword] = useState("");
  const [errors, setErrors] = useState<FieldErrors>({});
  const [submitting, setSubmitting] = useState(false);

  const updateField = (field: keyof FormValues, value: string) => {
    const nextValues: FormValues = {
      email: field === "email" ? value : email,
      name: field === "name" ? value : name,
      role: field === "role" ? (value as FormValues["role"]) : role,
      password: field === "password" ? value : password,
    };

    setErrors({ ...getErrors(nextValues) });
  };

  const hasFieldErrors = Boolean(
    errors.email || errors.name || errors.role || errors.password,
  );
  const canSubmit = Boolean(
    email.trim() &&
      name.trim() &&
      password.trim() &&
      !hasFieldErrors &&
      !submitting,
  );

  const handleSubmit = async (event: React.FormEvent<HTMLFormElement>) => {
    event.preventDefault();
    setErrors({});

    const nextErrors = getErrors({ email, name, role, password });
    if (Object.keys(nextErrors).length > 0) {
      setErrors(nextErrors);
      return;
    }

    setSubmitting(true);

    try {
      const response = await fetch("/api/users", {
        method: "POST",
