          name: name.trim(),
          role,
          password,
        }),
      });

      const result = await response.json();

      if (!response.ok || result.error) {
        setErrors({
          form: result.error?.message ?? "Unable to create the user.",
        });
        setSubmitting(false);
        return;
      }

      setEmail("");
      setName("");
      setRole("viewer");
      setPassword("");
      router.refresh();
