          createdAt: updatedTrip.createdAt.toISOString(),
          updatedAt: updatedTrip.updatedAt.toISOString(),
        },
        error: null,
      },
      { status: 200 },
    );
  } catch (error) {
    console.error("Failed to update trip", error);
    return jsonError(500, "INTERNAL_SERVER_ERROR", "Unable to update trip.");
  }
};

