type FieldErrors = {
  email?: string;
  name?: string;
  role?: string;
  password?: string;
  form?: string;
};

type FormValues = {
  email: string;
  name: string;
  role: "creator" | "administrator" | "viewer";
  password: string;
};

const roleOptions: Array<{ value: FormValues["role"]; label: string }> = [
