import type { KeyboardEvent as ReactKeyboardEvent, TouchEvent } from "react";
import { useEffect, useMemo, useRef, useState } from "react";
import { createPortal } from "react-dom";
import Image from "next/image";

type PhotoViewerImage = {
  url: string;
  alt: string;
};

type FullScreenPhotoViewerProps = {
  images: PhotoViewerImage[];
  initialIndex: number;
  isOpen: boolean;
  onClose: () => void;
  mode?: "viewer" | "slideshow";
};

type TouchLike = {
  clientX: number;
  clientY: number;
};

type TouchListLike = {
  length: number;
  [index: number]: TouchLike | undefined;
};

const clampIndex = (index: number, length: number) => {
  if (length <= 0) {
    return 0;
  }
  return Math.min(Math.max(index, 0), length - 1);
};

const isOptimizedImage = (url: string) => url.startsWith("/");

const getTouchDistance = (touches: TouchListLike) => {
  const [first, second] = [touches[0], touches[1]];
  if (!first || !second) {
    return 0;
  }
  const deltaX = first.clientX - second.clientX;
  const deltaY = first.clientY - second.clientY;
  return Math.hypot(deltaX, deltaY);
};

const FullScreenPhotoViewer = ({
  images,
  initialIndex,
  isOpen,
  onClose,
  mode = "viewer",
}: FullScreenPhotoViewerProps) => {
  const safeIndex = useMemo(
    () => clampIndex(initialIndex, images.length),
    [initialIndex, images.length],
  );
  const [activeIndex, setActiveIndex] = useState(safeIndex);
  const [zoomScale, setZoomScale] = useState(1);
  const [progressKey, setProgressKey] = useState(0);
  const [isPaused, setIsPaused] = useState(false);
  const dialogRef = useRef<HTMLDivElement | null>(null);
  const touchStartRef = useRef<{ x: number; y: number } | null>(null);
  const pinchStartRef = useRef<{ distance: number; scale: number } | null>(
    null,
  );
  const ignoreClickRef = useRef(false);
  const wasPausedRef = useRef(false);
  const isSlideshow = mode === "slideshow";

  useEffect(() => {
    if (!isOpen) {
      return;
    }
    // eslint-disable-next-line react-hooks/set-state-in-effect
    setActiveIndex(safeIndex);
    setZoomScale(1);
    setIsPaused(false);
  }, [isOpen, safeIndex]);

  useEffect(() => {
    if (!isOpen) {
      return;
    }
    dialogRef.current?.focus();
  }, [isOpen]);

  useEffect(() => {
    if (!isOpen) {
      return;
    }
    const previousOverflow = document.body.style.overflow;
    document.body.style.overflow = "hidden";
    return () => {
      document.body.style.overflow = previousOverflow;
    };
  }, [isOpen]);

  useEffect(() => {
    if (!isOpen) {
      return;
    }
    const handleKeyDown = (event: globalThis.KeyboardEvent) => {
      if (event.key === "Escape") {
        event.preventDefault();
        onClose();
        return;
      }
      if (
        isSlideshow &&
        (event.code === "Space" || event.key === " ")
      ) {
        event.preventDefault();
        setIsPaused((prev) => !prev);
        return;
      }
      if (event.key === "ArrowRight") {
        event.preventDefault();
        setActiveIndex((prev) =>
          clampIndex(prev + 1, images.length),
        );
        setZoomScale(1);
      }
      if (event.key === "ArrowLeft") {
        event.preventDefault();
        setActiveIndex((prev) =>
          clampIndex(prev - 1, images.length),
        );
        setZoomScale(1);
      }
    };

    window.addEventListener("keydown", handleKeyDown);
    return () => {
      window.removeEventListener("keydown", handleKeyDown);
    };
  }, [images.length, isOpen, isSlideshow, onClose]);

