from pathlib import Path
import re

impl_dir = Path('/Users/tommy/Development/TravelBlogs/_bmad-output/implementation-artifacts')
epics_path = impl_dir / 'epics.md'

text = epics_path.read_text()

story_re = re.compile(r'^### Story\s+(\d+)\.(\d+):\s*(.+)$', re.M)

def kebab(s):
    s = s.strip().lower()
    s = s.replace('&', 'and')
    s = re.sub(r'[^a-z0-9\s_-]', '', s)
    s = re.sub(r'\s+', '-', s)
    s = re.sub(r'-+', '-', s)
    return s

stories = []
for m in story_re.finditer(text):
    epic_num = m.group(1)
    story_num = m.group(2)
    title = m.group(3).strip()
    key = f"{epic_num}-{story_num}-{kebab(title)}"
    stories.append(key)

# Existing status file for preservation
status_path = impl_dir / 'sprint-status.yaml'
existing_status = {}
if status_path.exists():
    for line in status_path.read_text().splitlines():
        if ':' in line and line.strip() and not line.strip().startswith('#'):
            key, value = line.split(':', 1)
            key = key.strip()
            value = value.strip()
            if key and value:
                existing_status[key] = value

# Detect story files
story_files = {p.stem for p in impl_dir.glob('*.md') if p.name not in ['epics.md'] and not p.name.startswith('validation-report') and not (p.name.startswith('epic-') and 'retro' in p.name)}

status = {}
for key in stories:
    if key in story_files:
        status[key] = 'ready-for-dev'
    else:
        status[key] = 'backlog'

# Preserve advanced status
order = ['backlog', 'ready-for-dev', 'in-progress', 'review', 'done']
rank = {s: i for i, s in enumerate(order)}

for key, prev in existing_status.items():
    if key in status and prev in rank:
        if rank[prev] > rank[status[key]]:
            status[key] = prev

print('READY', sum(1 for v in status.values() if v == 'ready-for-dev'))
print('BACKLOG', sum(1 for v in status.values() if v == 'backlog'))

# Print story status map for later use
for key in stories:
    print(f"{key}\t{status[key]}")
