      data: {
        title: "New Trip",
        startDate: new Date("2025-01-01"),
        endDate: new Date("2025-01-05"),
        ownerId: "creator",
      },
    });

    expect(trip.title).toBe("New Trip");
    expect(trip.ownerId).toBe("creator");
    expect(trip.coverImageUrl).toBeNull();
  });
});

