    const trimmed = rawValue.trim();
    if (!trimmed) {
      return;
    }
    const normalized = normalizeTagName(trimmed);
    if (normalizedSelected.has(normalized)) {
      setError(t("entries.tagAlreadyAdded"));
      return;
    }
    setError(null);
    onChange([...value, trimmed]);
    setQuery("");
  };

